{
    "tiddlers": {
        "RenderSitemap": {
            "title": "RenderSitemap",
            "created": "20150104141300000",
            "creator": "Jim",
            "extension": ".xml",
            "modified": "20150104141300000",
            "modifier": "Jim",
            "tags": "static $:/tags/Exporter",
            "type": "text/vnd.tiddlywiki",
            "text": "\define renderContent()
<$text text=<<sitemapentries filter:"""$(exportFilter)$""">>/>
\end
<<renderContent>>"
        },
        "sitemap": {
            "title": "sitemap",
            "created": "20150104144500000",
            "creator": "Jim",
            "modified": "20150104144500000",
            "modifier": "Jim",
            "tags": "static",
            "type": "text/vnd.tiddlywiki",
            "text": "\define tv-wikilink-template() #$uri_encoded$
\define tv-config-toolbar-icons() no
\define tv-config-toolbar-text() no
\define tv-config-toolbar-class() tc-btn-invisible
`<?xml version="1.0" encoding="utf-8"?>
<urlset xmlns="http://www.sitemaps.org/schemas/sitemap/0.9">`<$set name="exportFilter" value="[!is[system]!title[Table of Contents]!sort[modifed]]">
{{RenderSitemap}}</$set>`</urlset>`"
        },
        "sitemapentries.js": {
            "title": "sitemapentries.js",
            "created": "20150104141600000",
            "creator": "Jim",
            "modified": "20150124151600000",
            "modifier": "Jim",
            "module-type": "macro",
            "tags": "static",
            "type": "application/javascript",
            "text": "/*\
Macro to output tiddlers matching a filter to sitemap.xml entries.
http://www.sitemaps.org/protocol.html
\*/
(function () {
  /*jslint node: true, browser: true */
  /*global $tw: false */
  'use strict';

  exports.name = 'sitemapentries';

  exports.params = [{ name: 'filter' }];

  let XML = {};

  XML.escapify = function (input) {
    return input
      .replace(/</gm, '&lt;')
      .replace(/>/gm, '&gt;')
      .replace(/&/gm, '&amp;')
      .replace(/"/gm, '&quot;')
      .replace(/'/gm, '&apos;');
  };

  XML.twDateToWebDate = function (twDate) {
    return $tw.utils.formatDateString(twDate, 'YYYY-0MM-0DD');
  };

  XML.stringify = function (data) {
    let x = '';
    data.forEach(function (element, index, array) {
      x += '\t<url>\n';
      const server = $tw.wiki.getTiddlerText('sitemapserver', '');
      x += `\t\t<loc>${server}#:${element.title.replace(/ /g, '%2520')}</loc>\n`;
      if (element.modified) {
        x += '\t\t<lastmod>' + XML.twDateToWebDate($tw.utils.parseDate(element.modified)) + '</lastmod>\n';
      }
      x += '\t\t<changefreq>';
      if (element.title.toLowerCase() === 'index' || element.title.toLowerCase() === 'home') {
        x += 'weekly';
      } else {
        x += 'monthly';
      }
      x += '</changefreq>\n';
      x += '\t\t<priority>';
      if (element.title.toLowerCase() === 'index' || element.title.toLowerCase() === 'home') {
        x += '1.0';
      } else if (element.color !== undefined) {
        x += '0.8';
      } else {
        x += '0.5';
      }
      x += '</priority>\n';
      x += '\t</url>\n';
    });
    return x;
  };

  exports.run = function (filter) {
    const tiddlers = this.wiki.filterTiddlers(filter);
    const data = [];
    for (let t = 0; t < tiddlers.length; t++) {
      const tiddler = this.wiki.getTiddler(tiddlers[t]);
      if (tiddler) {
        const fields = {};
        for (const field in tiddler.fields) {
          fields[field] = XML.escapify(tiddler.getFieldString(field));
        }
        data.push(fields);
      }
    }
    return XML.stringify(data);
  };
})();
"
        },
        "sitemapserver": {
            "title": "sitemapserver",
            "created": "20150104145100000",
            "creator": "Jim",
            "modified": "20150104145100000",
            "modifier": "Jim",
            "tags": "static",
            "type": "text/vnd.tiddlywiki",
            "text": "https://onetwo.ren/wiki/"
        }
    }
}
